import React from 'react';
import '../../App.css';
import { Link } from 'react-router-dom';

class SideNav extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            overallHovering: false,
            headerHovering: false,
            urbanHovering: false,
            landscapeHovering: false,
            portraitHovering: false
        }
        this.setHeaderHovering = this.setHeaderHovering.bind(this);
        this.setHeaderNotHovering = this.setHeaderNotHovering.bind(this);
        this.setOptionHovering = this.set
        this.setNotHovering = this.setNotHovering.bind(this);
    }

    setHeaderHovering() {
        this.setState(() => {
            return {
                headerHovering: true
            }
        })
    }

    setHeaderNotHovering() {
        this.setState(() => {
            return {
                headerHovering: false
            }
        })
    }

    setOptionHovering(option) {
        if (option === "Urban") {
            this.setState(() => {
                return {
                    urbanHovering: true
                }
            })
        }
        if (option === "Landscape") {
            this.setState(() => {
                return {
                    landscapeHovering: true
                }
            })
        }
        if (option === "Portrait") {
            this.setState(() => {
                return {
                    portraitHovering: true
                }
            })
        }
    }

    setOptionNotHovering(option) {
        if (option === "Urban") {
            this.setState(() => {
                return {
                    urbanHovering: false
                }
            })
        }
        if (option === "Landscape") {
            this.setState(() => {
                return {
                    landscapeHovering: false
                }
            })
        }
        if (option === "Portrait") {
            this.setState(() => {
                return {
                    portraitHovering: false
                }
            })
        }
    }

    setNotHovering() {
        this.setState(() => {
            return {
                overallHovering: headerHovering || urbanHovering || landscapeHovering || portraitHovering ? true : false
            }
        })
    }

    dropDown() {
        return (
            <ul>
                <Link onMouseOver={this.setOptionHovering("Urban")}
                    onMouseOut={this.setOptionNotHovering("Urban")}
                    to="/Gallery/Urban">
                    <li className="drop-down">Urban</li>
                </Link>
                <Link onMouseOver={this.setOptionHovering("Landscape")}
                    onMouseOut={this.setOptionNotHovering("Landscape")}
                    to="/Gallery/Landscape">
                    <li className="drop-down">Landscape</li>
                </Link>
                <Link onMouseOver={this.setOptionHovering("Portrait")}
                    onMouseOut={this.setOptionNotHovering("Portrait")}
                    to="/Gallery/Portraits">
                    <li className="drop-down">Portraits</li>
                </Link>
            </ul>
        )
    }

    toRender() {
        return (
            <ul>
                <Link to="/">
                    <li>Home</li>
                </Link>
                <Link to="/AboutMe">
                    <li>About Me</li>
                </Link>
                <Link onMouseOver={this.setHeaderHovering}
                    onMouseOut={this.setHeaderNotHovering}>
                    <li>Gallery</li>
                    {this.state.headerHovering ? this.dropDown() : ""}
                </Link>
                <Link to="/Contact">
                    <li>Contact</li>
                </Link>
            </ul>
        )
    }

    render() {
        return (
            <nav className={this.props.openNav ? "side-nav" : ""}>
                {this.props.openNav ? this.toRender() : ""}
            </nav>
        )
    }
}

export default SideNav;
