import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import Contact from './Components/Options/Contact';
import AboutMe from './Components/Options/AboutMe';
import * as serviceWorker from './serviceWorker';
import { HashRouter as Router, Switch, Route } from 'react-router-dom';
import axios from "axios";
import "react-image-gallery/styles/css/image-gallery.css";
import ImageGallery from "react-image-gallery";

ReactDOM.render(
  <React.StrictMode>
    <Router>
    <App />
    </Router>
  </React.StrictMode>,
  document.getElementById('root')
);

// START
async function getAlbum(id) {
  const response = await axios.get(`https://photos.app.goo.gl/${id}`)
  return response.data 
 }

 const regex = /\["(https:\/\/lh3\.googleusercontent\.com\/[a-zA-Z0-9\-_]*)"/g // the only difference is the [ at the beginning
 function extractPhotos(content) {
   const links = new Set()
   let match
   while (match = regex.exec(content)) {
     links.add(match[1])
   } 
   return Array.from(links)
 }

 const ImagesGallery = () => {
  const [images, setImages] = React.useState(null)
  React.useEffect(() => {
    let shouldCancel = false
    const call = async () => {
      const response = await axios.get(
        'https://google-photos-album-demo.glitch.me/4eXXxxG3rYwQVf948'
      );
      if (!shouldCancel && response.data && response.data.length > 0) {
        setImages(response.data.map(url => ({
          original: `${url}=w1024`,
          thumbnail: `${url}=w100`
        })))
      }
    }
    call()
    return () => shouldCancel = true
  }, [])
  return images ? <ImageGallery items={images} /> : null
}
export default ImagesGallery
// END

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
