import React from 'react';
import '../../App.css';
import { Link } from 'react-router-dom';

class SideNav extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            headerHovering: false,
            urbanHovering: false,
            landscapeHovering: false,
            portraitHovering: false
        }
        this.setHeaderHovering = this.setHeaderHovering.bind(this);
        this.setNotHovering = this.setNotHovering.bind(this);
    }

    setHeaderHovering() {
        this.setState(() => {
            return {
                headerHovering: true
            }
        })

    }

    setNotHovering() {
        this.setState(() => {
            return {
                headerHovering: false
            }
        })
    }

    dropDown() {
        return (
            <ul>
                <Link onMouseOver={this.setOptionHovering(Gallery)} 
                    to="/Gallery/Urban">
                    <li className="drop-down">Urban</li>
                </Link>
                <Link to="/Gallery/Landscape">
                    <li className="drop-down">Landscape</li>
                </Link>
                <Link to="/Gallery/Portraits">
                    <li className="drop-down">Portraits</li>
                </Link>
            </ul>
        )
    }

    toRender() {
        return (
            <ul>
                <Link to="/">
                    <li>Home</li>
                </Link>
                <Link to="/AboutMe">
                    <li>About Me</li>
                </Link>
                <Link onMouseOver={this.setHeaderHovering}
                    onMouseOut={this.setNotHovering}>
                    <li>Gallery</li>
                    {this.state.headerHovering ? this.dropDown() : ""}
                </Link>
                <Link to="/Contact">
                    <li>Contact</li>
                </Link>
            </ul>
        )
    }

    render() {
        return (
            <nav className={this.props.openNav ? "side-nav" : ""}>
                {this.props.openNav ? this.toRender() : ""}
            </nav>
        )
    }
}

export default SideNav;
